import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useTable } from 'react-table';
import { useHistory } from 'react-router-dom'; // React Router import

import NavBar from '../patials/navbar';
import SideBar from '../patials/sidebar';

const Home = () => {
  const [machines, setMachines] = useState([]);
  const token = sessionStorage.getItem('token');
  const history = useHistory(); // Instance de history pour la redirection

  useEffect(() => {
    const fetchMachines = async () => {
      try {
        if (token) {
          const response = await axios.get(`http://localhost:8000/machines/${token}/`, {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });
          setMachines(response.data.data || []);
        }
      } catch (error) {
        console.error('Error fetching machines:', error);
      }
    };

    fetchMachines();
  }, [token]);

  const machineColumns = [
    { Header: 'ID', accessor: 'id' },
    { Header: 'Type', accessor: 'machine_type' },
    { Header: 'MAC Address', accessor: 'mac_address' },
    { Header: 'System', accessor: 'system' },
    { Header: 'Node Name', accessor: 'node_name' },
    { Header: 'Architecture', accessor: 'machine_architecture' },
    { Header: 'Processor', accessor: 'processor' },
    { Header: 'Cores', accessor: 'cores' },
    { Header: 'Logical Cores', accessor: 'logical_cores' },
    { Header: 'CPU Frequency', accessor: 'cpu_frequency' },
    { Header: 'Total Memory', accessor: 'total_memory' },
    { Header: 'Total Disk', accessor: 'total_disk' },
    { Header: 'Version', accessor: 'version' },
    { Header: 'Releases', accessor: 'releases' },
    { Header: 'Collected At', accessor: 'collected_at' },
    { Header: 'Timestamp', accessor: 'timestamp' },
  ];

  // Fonction pour gérer la redirection vers la page de détails
  const redirectToDetails = (id) => {
    console.log(`Redirect to details for machine with ID ${id}`);
    // Exemple de redirection en utilisant React Router
    history.push(`/machine-details/${id}`);
  };

  const DataTable = ({ columns, data }) => {
    const {
      getTableProps,
      getTableBodyProps,
      headerGroups,
      rows,
      prepareRow,
    } = useTable({ columns, data });

    // Vérifier si les données sont définies
    if (!data || data.length === 0) {
      return <p>Loading...</p>; // Message de chargement ou indicateur visuel
    }

    return (
      <table {...getTableProps()} className="table">
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
              ))}
              <th>Actions</th> {/* Colonne supplémentaire pour les actions */}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                ))}
                <td>
                  <div className="dropdown">
                    <button
                      className="btn btn-secondary dropdown-toggle"
                      type="button"
                      id={`dropdownMenuButton${row.original.id}`}
                      data-toggle="dropdown"
                      aria-haspopup="true"
                      aria-expanded="false"
                    >
                      &#8226;&#8226;&#8226;
                    </button>
                    <div className="dropdown-menu" aria-labelledby={`dropdownMenuButton${row.original.id}`}>
                      <button
                        className="dropdown-item"
                        onClick={() => redirectToDetails(row.original.id)}
                      >
                        Caractéristiques
                      </button>
                      {/* Autres actions si nécessaire */}
                    </div>
                  </div>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  };

  return (
    <>
      <NavBar />
      <SideBar />
      <main id="main" className="main">
        <div className="pagetitle">
          <h1>Machine Data</h1>
          <nav>
            <ol className="breadcrumb">
              <li className="breadcrumb-item"><a href="index.html">Home</a></li>
              <li className="breadcrumb-item">Pages</li>
              <li className="breadcrumb-item active">Machine Data</li>
            </ol>
          </nav>
        </div>
        <section className="section">
          <div className="row">
            <div className="col-lg-6">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Machines</h5>
                  <DataTable columns={machineColumns} data={machines} />
                </div>
              </div>
            </div>
            {/* Ajoutez ici d'autres colonnes de cartes si nécessaire */}
          </div>
        </section>
      </main>
      <footer id="footer" className="footer">
        <div className="copyright">
          &copy; Copyright <strong><span>NiceAdmin</span></strong>. All Rights Reserved
        </div>
        <div className="credits">
          Designed by <a href="https://bootstrapmade.com/">Jbbonice2 && SergeNoah000</a>
        </div>
      </footer>
      <a href="#" className="back-to-top d-flex align-items-center justify-content-center"><i className="bi bi-arrow-up-short"></i></a>
    </>
  );
};

export default Home;
